declare module "*.css"{
    const css: any;
    export default css;
}

declare module "*.scss"{
    const content: any;
    export default content;
}
declare module "*.sass"{
    const content: any;
    export default content;
}
declare module "*.png"{
    const content: any;
    export = content;
}
declare module "*.svg"{
    const content: any;
    export default content;
}